(() => {
  'use strict';

  function init() {
    const footerCurrentYear = document.getElementById(`footer-current-year`);
    if (footerCurrentYear) {
      footerCurrentYear.innerText = new Date().getFullYear().toString();
    }
  }

  document.readyState !== `loading` ? init() : document.addEventListener(`readystatechange`, () => {document.readyState === `complete` && init()});

})()